            binding = bindings.getBindingContext().bindValue(WidgetProperties.text(SWT.Modify).observe(value), observable, new UpdateValueStrategy().setAfterGetValidator(input2model).setConverter(input2model), new UpdateValueStrategy().setConverter(new ToAttributeStringConverter(attribute)));
