    public static Collection<URI> getStorageDirs(Configuration conf, String propertyName) {
        Collection<String> dirNames = conf.getStringCollection(propertyName);
        StartupOption startOpt = NameNode.getStartupOption(conf);
        if (startOpt == StartupOption.IMPORT) {
            Configuration cE = new HdfsConfiguration(false);
            cE.addResource("core-default.xml");
            cE.addResource("core-site.xml");
            cE.addResource("hdfs-default.xml");
            Collection<String> dirNames2 = cE.getStringCollection(propertyName);
            dirNames.removeAll(dirNames2);
            if (dirNames.isEmpty())
                LOG.warn("!!! WARNING !!!" + "\n\tThe NameNode currently runs without persistent storage." + "\n\tAny changes to the file system meta-data may be lost." + "\n\tRecommended actions:" + "\n\t\t- shutdown and restart NameNode with configured \"" + propertyName + "\" in hdfs-site.xml;" + "\n\t\t- use Backup Node as a persistent and up-to-date storage " + "of the file system meta-data.");
        } else if (dirNames.isEmpty())
            dirNames.add("/tmp/hadoop/dfs/name");
        Collection<URI> dirs = new ArrayList<URI>(dirNames.size());
        for (String name : dirNames) {
            try {
                URI u = new URI(name);
                if (u.getScheme() == null) {
                    u = new URI("file://" + new File(name).getAbsolutePath());
                    LOG.warn("Scheme is undefined for " + name);
                    LOG.warn("Please check your file system configuration in " + "hdfs-site.xml");
                }
                dirs.add(u);
            } catch (Exception e) {
                LOG.error("Error while processing URI: " + name + ". The error message was: " + e.getMessage());
            }
        }
        return dirs;
    }
