    public LocatedBlock getAdditionalBlock(String src, String clientName, Block previous, HashMap<Node, Node> excludedNodes) throws IOException {
        long fileLength, blockSize;
        int replication;
        DatanodeDescriptor clientNode = null;
        Block newBlock = null;
        NameNode.stateChangeLog.debug("BLOCK* NameSystem.getAdditionalBlock: file " + src + " for " + clientName);
        synchronized (this) {
            if (isInSafeMode()) {
                throw new SafeModeException("Cannot add block to " + src, safeMode);
            }
            checkFsObjectLimit();
            INodeFileUnderConstruction pendingFile = checkLease(src, clientName);
            blockManager.commitLastBlock(pendingFile, previous);
            if (!checkFileProgress(pendingFile, false)) {
                throw new NotReplicatedYetException("Not replicated yet:" + src);
            }
            fileLength = pendingFile.computeContentSummary().getLength();
            blockSize = pendingFile.getPreferredBlockSize();
            clientNode = pendingFile.getClientNode();
            replication = (int) pendingFile.getReplication();
        }
        DatanodeDescriptor[] targets = blockManager.replicator.chooseTarget(src, replication, clientNode, excludedNodes, blockSize);
        if (targets.length < blockManager.minReplication) {
            throw new IOException("File " + src + " could only be replicated to " + targets.length + " nodes, instead of " + blockManager.minReplication + ". ( there are currently " + heartbeats.size() + " live data nodes in the cluster)");
        }
        synchronized (this) {
            INode[] pathINodes = dir.getExistingPathINodes(src);
            int inodesLen = pathINodes.length;
            checkLease(src, clientName, pathINodes[inodesLen - 1]);
            INodeFileUnderConstruction pendingFile = (INodeFileUnderConstruction) pathINodes[inodesLen - 1];
            if (!checkFileProgress(pendingFile, false)) {
                throw new NotReplicatedYetException("Not replicated yet:" + src);
            }
            blockManager.completeBlock(pendingFile, pendingFile.numBlocks() - 2);
            newBlock = allocateBlock(src, pathINodes, targets);
            for (DatanodeDescriptor dn : targets) {
                dn.incBlocksScheduled();
            }
        }
        LocatedBlock b = new LocatedBlock(newBlock, targets, fileLength);
        if (isAccessTokenEnabled) {
            b.setAccessToken(accessTokenHandler.generateToken(b.getBlock().getBlockId(), EnumSet.of(AccessTokenHandler.AccessMode.WRITE)));
        }
        return b;
    }

