        String path = ioUtils.getRelativePath(src, configuration.getSrcDir());
        if (configuration.exclude(path))
            return;
        if (src.isDirectory()) {
            for (String file : src.list(filter)) copyFolder(new File(src, file), new File(dest, file), filter, instrument);
